declare module '*.css' {
  const classNames: Record<string, string>;
  export default classNames;
}

declare module '*.gif' {
  const image: string;
  export default image;
}

declare module '*.html' {
  const html: string;
  export default html;
}

declare module '*.ico' {
  const image: string;
  export default image;
}

declare module '*.jpeg' {
  const image: string;
  export default image;
}

declare module '*.jpg' {
  const image: string;
  export default image;
}

declare module '*.png' {
  const image: string;
  export default image;
}

declare module '*.svg' {
  const image: any;
  export default image;
}
